				\setlength{\oddsidemargin}{-69pt}
				\begin{document}$$1.0\times$$\end{document}1.0×, \documentclass[12pt]{minimal}
				\usepackage{amsmath}
				\usepackage{wasysym} 
				\usepackage{amsfonts} 
				\usepackage{amssymb} 
				\usepackage{amsbsy}
				\usepackage{mathrsfs}
				\usepackage{upgreek}
				\setlength{\oddsidemargin}{-69pt}
				\begin{document}$$0.75\times$$\end{document}0.75×, \documentclass[12pt]{minimal}
